function check_value($postID) {\n    $ID = $postID;\n    $cookie = $_COOKIE['list_of_IDS'];\n    \n    // Trim the ID and cookie to remove any unwanted spaces\n    $ID = trim($ID);\n    $cookie = trim($cookie);\n\n    echo 'ID: '.$ID.' - Cookie: '.$cookie . "\n";\n\n    // Add a period at the end of the ID if it's part of the format in the cookie\n    $position = strpos($cookie, $ID . '.');\n\n    if ($position !== false) {\n        echo "ID is in the cookie.\n";\n    } else {\n        echo "ID is NOT in the cookie.\n";\n    }\n}