function quicksort($array) {\n    if (count($array) < 2) {\n        return $array;\n    }\n    $left = $right = array();\n    reset($array);\n    $pivot_key = key($array);\n    $pivot = array_shift($array);\n    foreach ($array as $k => $v) {\n        if ($v < $pivot)\n            $left[$k] = $v;\n        else\n            $right[$k] = $v;\n    }\n    return array_merge(quicksort($left), array($pivot_key => $pivot), quicksort($right));\n}