function markThreadAsRead($userId, $threadId) {\n    global $conn; // Assume this is your MySQLi connection\n    $stmt = $conn->prepare("INSERT INTO UserReadThreads (UserID, ThreadID) VALUES (?, ?)\n                            ON DUPLICATE KEY UPDATE LastReadDateTime=VALUES(LastReadDateTime)");\n    if ($stmt === false) { die('Prepare statement failed'); }\n    $stmt->bind_param("ii", $userId, $threadId);\n    $stmt->execute();\n}