function get_content($a, $b, $c = "00") {\n    $request  = "arg01="  . $a;\n    $request .= "&arg02="  . $b;\n    $request .= "&arg03="  . $c;\n\n    $host = "www.site.com";\n    $script = "/page.php";\n    $method = "POST";\n\n    $request_length = strlen($request);\n\n    $header = "$method $script HTTP/1.1\r\n";\n    $header .= "Host: $host\r\n";\n    $header .= "Content-Type: application/x-www-form-urlencoded\r\n";\n    $header .= "Content-Length: $request_length\r\n";\n    $header .= "Connection: close\r\n\r\n";\n    $header .= "$request\r\n";\n\n    $socket = @fsockopen($host, 80, $errno, $errstr);\n    if ($socket) {\n        fputs($socket, $header);\n        $response = '';\n        while (!feof($socket)) {\n            $response .= fgets($socket);\n        }\n        fclose($socket);\n\n        // Parse chunked response\n        list($headers, $body) = explode("\r\n\r\n", $response, 2);\n        if (preg_match('/Transfer-Encoding: chunked/i', $headers)) {\n            $output = '';\n            while ($body !== '0') {\n                list($chunkSizeHex, $body) = explode("\r\n", $body, 2);\n                $chunkSize = hexdec(trim($chunkSizeHex));\n                $output .= substr($body, 0, $chunkSize);\n                $body = substr($body, $chunkSize + 2);\n            }\n        } else {\n            $output = $response;\n        }\n    }\n\n    return $output;\n}