<?php\n\nfunction getTopGoogleSearchResult($query, $apiKey, $searchEngineId) {\n    // Set up the API endpoint and parameters\n    $url = 'https://www.googleapis.com/customsearch/v1?q=' . urlencode($query) . '&key=' . urlencode($apiKey) . '&cx=' . urlencode($searchEngineId);\n    \n    // Initialize a CURL session\n    $ch = curl_init();\n    curl_setopt($ch, CURLOPT_URL, $url);\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\n    \n    // Execute the request and close the cURL session\n    $response = curl_exec($ch);\n    curl_close($ch);\n    \n    // Decode JSON response\n    $data = json_decode($response, true);\n    \n    // Check if we have results\n    if (!empty($data['items'])) {\n        return $data['items'][0]['link'];\n    }\n    \n    return null;\n}\n\n// Sample usage\n$apiKey = 'YOUR_API_KEY'; // Replace with your API Key\n$searchEngineId = 'YOUR_SEARCH_ENGINE_ID'; // Replace with your Search Engine ID\n\n$query = "PHP Security Guide";\n$topResultUrl = getTopGoogleSearchResult($query, $apiKey, $searchEngineId);\n\nif ($topResultUrl) {\n    header("Location: " . $topResultUrl);\n    exit();\n} else {\n    echo 'No results found.';\n}\n?>