function fetch($get, $put) {\n    // Check if required functions are available\n    if (!function_exists('fopen') || !ini_get('allow_url_fopen')) {\n        throw new Exception("fopen is not enabled or allow_url_fopen is disabled.");\n    }\n\n    // Open the source file for reading\n    $source = fopen($get, 'r');\n    if (!$source) {\n        throw new Exception("Failed to open remote file.");\n    }\n\n    // Open the destination file for writing\n    $destination = fopen($put, 'w');\n    if (!$destination) {\n        fclose($source);\n        throw new Exception("Failed to open local file.");\n    }\n\n    // Copy data from the source to the destination\n    $result = stream_copy_to_stream($source, $destination);\n\n    // Close both files\n    fclose($source);\n    fclose($destination);\n\n    if ($result === false) {\n        throw new Exception("Failed to copy data from remote file to local file.");\n    }\n}