<?php\n// Sample data: array of users and their visited pages\n$data = [\n    ['user' => 'Alice', 'pages' => ['home', 'about']],\n    ['user' => 'Bob', 'pages' => ['contact', 'services']],\n];\n\necho '<table border="1"><tr><th>User</th><th>Pages Visited</th></tr>';\nforeach ($data as $row) {\n    echo "<tr><td>{$row['user']}</td><td>" . implode(', ', $row['pages']) . "</td></tr>";\n}\necho '</table>';\n?>