<?php\nfunction isBot() {\n    $bots = array(\n        'Googlebot', // Google\n        'Bingbot',   // Bing\n        'Slurp',     // Yahoo\n        'DuckDuckBot'// DuckDuckGo\n        // Add more bots as necessary\n    );\n\n    foreach ($bots as $bot) {\n        if(stripos($_SERVER['HTTP_USER_AGENT'], $bot) !== false) {\n            return $bot;\n        }\n    }\n\n    return false;\n}\n\nfunction logLastVisit($botName) {\n    $logFile = 'bot_visits.log';\n    $timestamp = date('Y-m-d H:i:s');\n    file_put_contents($logFile, "$botName visited on $timestamp\n", FILE_APPEND);\n}\n\n$botName = isBot();\nif ($botName) {\n    logLastVisit($botName);\n}\n?>