<?php\n$username = $_POST['username'];\n$password = $_POST['password'];\n\n// Use cURL or file_get_contents to send data to the login server\n$ch = curl_init();\ncurl_setopt($ch, CURLOPT_URL, "http://clientlogindomain.com/login");\ncurl_setopt($ch, CURLOPT_POST, true);\ncurl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query(array('username' => $username, 'password' => $password)));\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n\n$response = curl_exec($ch);\nif(curl_errno($ch)){\n    echo 'Curl error: ' . curl_error($ch);\n}\ncurl_close($ch);\n\n// Echo the response back to your Flash app\necho $response;\n?>