function mytheme_default_content( $post ) {\n    if ( !empty( $_GET['post_type'] ) && 'post' == $_GET['post_type'] ) {\n        add_filter('default_content', 'modify_new_post_content');\n    }\n}\n\nfunction modify_new_post_content($content) {\n    $defaultContent = "<p>Your standard header goes here.</p>";\n    return $defaultContent." ".$content;\n}\n\nadd_action( 'load-post.php', 'mytheme_default_content' );