if(isset($_GET["pk"]) && !empty(trim($_GET["pk"]))) {\n    $pk = trim($_GET["pk"]);\n    // Use prepared statements to prevent SQL injection\n    $stmt = $conn->prepare("SELECT * FROM Auctions WHERE ARTICLE_NO = ?");\n    $stmt->bind_param("s", $pk);\n    $stmt->execute();\n    $result = $stmt->get_result();\n    // Process the result...\n} else {\n    echo "Invalid or missing pk parameter.";\n}