while ($item = mysqli_fetch_array($results)) {\n    // Check if the string contains any Arabic characters\n    $hasArabicGlyphs = preg_match('/\p{Arabic}/u', $item['item_title']);\n    \n    if ($hasArabicGlyphs) {\n        $transliterated = trim(strtolower($Ar->ar2en($item['item_title'])));\n        echo "<h3>" . htmlspecialchars($item['item_title']) . " <em>(" . htmlspecialchars($transliterated) . ")</em></h3>";\n    } else {\n        echo "<h3>" . htmlspecialchars($item['item_title']) . "</h3>";\n    }\n}