<?php\n// Password hashing example\n\n$password = "user_secure_password";\n\n// Hash password\n$hashedPassword = password_hash($password, PASSWORD_BCRYPT);\n\n// Store $hashedPassword in your database not the original password\n\n// Example of verifying the password (e.g., on login)\n$inputPassword = "user_entered_password"; // This would come from a form submission\nif (password_verify($inputPassword, $hashedPassword)) {\n    echo 'Password is valid!';\n} else {\n    echo 'Invalid password.';\n}\n?>