$handle = @fopen($fullPath, "r");\nif ($handle) {\n    while (!feof($handle)) {\n        $buffer = fgets($handle);\n        // Remove any existing \r characters to prevent duplication\n        $buffer = str_replace("\r", "", $buffer);\n        // Replace \n with \r\n for Windows compatibility\n        $buffer = str_replace("\n", "\r\n", $buffer);\n        echo $buffer;\n    }\n    fclose($handle);\n}