<?php\n\n$url = "http://www.someaddress.ext/path/";\n$ch = curl_init($url);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n\n$response = curl_exec($ch);\ncurl_close($ch);\n\n// Assuming the page lists files in anchor tags <a href="...">...</a>\npreg_match_all('/<a\s+href=["\'](.*?)\.pk3["\']>/', $response, $matches);\n\nif (!empty($matches[1])) {\n    foreach ($matches[1] as $file) {\n        $fullUrl = $url . basename($file);\n        file_put_contents(basename($file), file_get_contents($fullUrl));\n        echo "Downloaded: $fullUrl\n";\n    }\n} else {\n    echo "No .pk3 files found.\n";\n}\n\n?>