<?php\n// Sanitize input to mitigate directory traversal risks\n$folder = empty($_GET['t']) ? "apps/" : basename(sanitizeInput($_GET['t'])) . "/";\n\n$app = isset($_POST['app']) ? sanitizeInput($_POST['app']) : (isset($_GET['app']) ? sanitizeInput($_GET['app']) : 'home');\n\nif (file_exists($folder . $app . ".php")) {\n    include($folder . $app . ".php");\n} else {\n    include("/home/radonsys/public_html/global/error/404.php");\n}\n\nfunction sanitizeInput($data) {\n    // Basic sanitization\n    return htmlspecialchars(strip_tags(trim($data)));\n}\n?>