<?php\nfunction contains_invalid_characters($text) {\n    // HTML entities decoding loop (up to 3 times)\n    for ($i = 0; $i < 3; $i++) {\n        $text = html_entity_decode($text, ENT_QUOTES);\n    }\n    \n    // Define the pattern for valid characters: a-z, A-Z, 0-9, äöüß, space, and specified special characters\n    $pattern = '/^[a-zA-Z0-9äöüß !§$%&\/()=\[\]\?.:,;-_]+$/';\n    \n    // Check if the text contains only valid characters\n    return !preg_match($pattern, $text);\n}\n\n// Example usage:\n$text = "Hello Wörld!";\nif (contains_invalid_characters($text)) {\n    echo "The text contains invalid characters.";\n} else {\n    echo "The text is valid.";\n}\n?>