// Simple MVC Example\n\n// Model (Assuming already exists)\nclass UserModel {\n    public function getUser($userId) {\n        // Logic to fetch user from database\n    }\n}\n\n// View\nclass UserView {\n    public function showUser(User $user) {\n        echo "Name: " . htmlspecialchars($user->name);\n        echo "<br>Email: " . htmlspecialchars($user->email);\n    }\n}\n\n// Controller\nclass UserController {\n    private $model;\n    private $view;\n\n    public function __construct($model, $view) {\n        $this->model = $model;\n        $this->view = $view;\n    }\n\n    public function displayUser($userId) {\n        $user = $this->model->getUser($userId);\n        if ($user) {\n            $this->view->showUser($user);\n        } else {\n            echo "User not found.";\n        }\n    }\n}\n\n// Usage\n$userModel = new UserModel();\n$userView = new UserView();\n$userController = new UserController($userModel, $userView);\n\n// Simulate displaying a user with ID 1\n$userController->displayUser(1);