<?php\n// Define the path to store images\n$dir = 'generated_images/';\n$file_name = $_GET['img'];\n$path = $dir . $file_name;\n\nif (!file_exists($path)) {\n    // Check if directory exists, create it if not\n    if (!is_dir($dir)) {\n        mkdir($dir);\n    }\n\n    // Create the image (here we demonstrate with a blank white image)\n    $im = imagecreatetruecolor(100, 40);\n    $white = imagecolorallocate($im, 255, 255, 255);\n    $black = imagecolorallocate($im, 0, 0, 0);\n\n    // Write some text on the image\n    imagestring($im, 1, 7, 5, $file_name, $black);\n    imagejpeg($im, $path);\n\n    imagedestroy($im);\n}\n\n// Output the header and content\nheader('Content-Type: image/jpeg');\nreadfile($path);