<?php\n$descriptorSpec = array(\n    0 => STDIN,   // stdin is a pipe that the child will read from\n    1 => STDOUT,  // stdout is a pipe that the child will write to\n    2 => STDERR   // stderr is a pipe that the child will write to\n);\n\n$cwd = '/path/to/sandbox/dir';  // Change this to your desired working directory\n\n// PHP command with script to execute\n$cmd = 'php /path/to/script.php';\n\n$process = proc_open($cmd, $descriptorSpec, $pipes, $cwd);\n\nif (is_resource($process)) {\n    # Fetch output from the process\n    fclose($pipes[0]);\n\n    echo stream_get_contents($pipes[1]);\n    fclose($pipes[1]);\n\n    echo stream_get_contents($pipes[2]);\n    fclose($pipes[2]);\n\n    proc_close($process);\n}\n?>