// Step 1: Pre-process $_GET\nif (!isset($_GET['name']) || !trim($_GET['name'])) {\n    $_GET['name'] = '(default name)';\n}\n\n// Remove spaces manually if needed\n$_GET['name'] = str_replace(' ', '', $_GET['name']);\n\n// Step 2: Use filter_input to sanitize and validate\n$name = filter_input(INPUT_GET, 'name', FILTER_SANITIZE_STRING);\n\n// Additional filters can be added as needed\n$state = filter_input(INPUT_GET, 'state', FILTER_SANITIZE_STRING);\nif ($state === '') {\n    $state = 'AL'; // default state\n}\n\necho "Name: " . htmlspecialchars($name) . "<br>"; // always escape output for security\necho "State: " . htmlspecialchars($state) . "<br>";