$cookieString = $_SERVER['HTTP_COOKIE'];\n$opts = [\n    'http' => [\n        'method' => 'GET',\n        'header' => "Cookie: $cookieString\r\n"\n    ]\n];\n\n$context  = stream_context_create($opts);\n$contents = file_get_contents("http://www.example.com/test1.php", false, $context);\n\nif ($contents === FALSE) {\n    // Handle error\n    echo "Failed to retrieve contents.\n";\n} else {\n    echo $contents;\n}