function getIncompleteClassProperties($incompleteObject) {\n    $reflectionClass = new ReflectionClass($incompleteObject);\n    $properties = [];\n\n    foreach ($reflectionClass->getProperties() as $property) {\n        $property->setAccessible(true);\n        $properties[$property->getName()] = $property->getValue($incompleteObject);\n    }\n\n    return $properties;\n}\n\n// Usage\n$objectProperties = getIncompleteClassProperties($yourIncompleteObject);\nprint_r($objectProperties);