<?php\n   // Include the autoload file if you're using Composer\n   require 'vendor/autoload.php';\n\n   use Parsedown;\n\n   function convertToHTML($text) {\n       $parsedown = new Parsedown();\n\n       // Convert markdown to HTML\n       $html = $parsedown->parse($text);\n\n       // Sanitize output (optional but recommended)\n       $sanitizedHtml = filter_var($html, FILTER_SANITIZE_FULL_SPECIAL_CHARS, FILTER_FLAG_NO_ENCODE_QUOTES);\n\n       return $sanitizedHtml;\n   }\n\n   // Example usage:\n   $inputText = "Hello **World**! Here's a link to [OpenAI](https://www.openai.com).";\n\n   $safeHTML = convertToHTML($inputText);\n   echo "<div>$safeHTML</div>";\n   ?>