<?php\nfunction replaceWordWithSubstring($inputString, $searchSubstring, $replacement) {\n    // Create a regular expression pattern that matches whole words containing the search substring\n    $pattern = '/\b\w*' . preg_quote($searchSubstring, '/') . '\w*\b/';\n    \n    // Use preg_replace to replace all occurrences of the matched words with the replacement string\n    return preg_replace($pattern, $replacement, $inputString);\n}\n\n// Example usage:\n$inputString1 = "lorem ipsum";\n$result1 = replaceWordWithSubstring($inputString1, "lo", "lorem can");\necho $result1 . "\n"; // Outputs: lorem can ipsum\n\n$inputString2 = "loreal ipsum";\n$result2 = replaceWordWithSubstring($inputString2, "lo", "lorem can");\necho $result2; // Outputs: loreal can ipsum\n?>