<?php\n$backupFile = 'C:\\path\\to\\your\\db-backup-file.bak';\n$dbName = 'your_database_name';\n\nexec("sqlcmd -S localhost -U sa -P your_password -Q \"BACKUP DATABASE [$dbName] TO DISK=N'$backupFile' WITH NOFORMAT, MEDIADESCRIPTION=N'Disk Backup of ". escapeshellarg($dbName) . "', SKIP, NOREWIND, NOUNLOAD, STATS=10\"", $output, $returnVar);\nif ($returnVar === 0) {\n    echo "Backup successful!";\n} else {\n    echo "Error during backup: " . implode("\n", $output);\n}\n?>