<?php\n\nfunction fetchWikipediaInfobox($title) {\n    $apiUrl = "https://en.wikipedia.org/w/api.php";\n    $params = [\n        'action' => 'query',\n        'titles' => $title,\n        'prop' => 'revisions',\n        'rvslots' => '*',\n        'rvprop' => 'content',\n        'format' => 'json'\n    ];\n\n    $url = $apiUrl . '?' . http_build_query($params);\n    $ch = curl_init();\n    curl_setopt_array($ch, [\n        CURLOPT_URL => $url,\n        CURLOPT_RETURNTRANSFER => true\n    ]);\n    \n    $output = curl_exec($ch);\n    curl_close($ch);\n    \n    $result = json_decode($output, true);\n    foreach ($result['query']['pages'] as $page) {\n        return htmlspecialchars_decode($page['revisions'][0]['slots']['*']['*']);\n    }\n}\n\nfunction parseInfobox($wikitextContent) {\n    // This is a simple parser that may need adjustment for complex infoboxes\n    if (preg_match('/{{Infobox[^}]*}}/s', $wikitextContent, $matches)) {\n        return htmlspecialchars_decode($matches[0]);\n    }\n    return null;\n}\n\nfunction saveToDatabase($title, $infobox) {\n    // Connect to MySQL and insert/update the data\n    // Placeholder function - replace with actual database logic\n    echo "Saving Infobox for $title: \n";\n    echo $infobox . "\n";\n}\n\n// Example usage\n$cityTitle = "Beijing";\n$countryTitle = "China";\n\n$rawContentCity = fetchWikipediaInfobox($cityTitle);\n$infoboxCity = parseInfobox($rawContentCity);\n\n$rawContentCountry = fetchWikipediaInfobox($countryTitle);\n$infoboxCountry = parseInfobox($rawContentCountry);\n\nsaveToDatabase($cityTitle, $infoboxCity);\nsaveToDatabase($countryTitle, $infoboxCountry);\n\n?>