class Users {\n    public static function getUsers($criteria) {\n        $url = "https://api.example.com/users?$criteria";\n        $response = file_get_contents($url);\n        return json_decode($response, true);\n    }\n\n    public static function addUser($token, $username, $password, $firstName, $lastName) {\n        $data = [\n            'username' => $username,\n            // ... other fields\n        ];\n        $json_data = json_encode($data);\n\n        $options = [\n            'http' => [\n                'header'  => "Content-type: application/json\r\nAuthorization: Bearer $token\r\n",\n                'method'  => 'POST',\n                'content' => $json_data,\n            ],\n        ];\n        $context  = stream_context_create($options);\n        return file_get_contents("https://api.example.com/users", false, $context);\n    }\n}