class Users {\n    private $soapClient;\n\n    public function __construct() {\n        $this->soapClient = new SoapClient('http://localhost/test/webservice_user.php?wsdl', [\n            'trace' => 1,\n            'exceptions' => true,\n        ]);\n    }\n\n    public function getUsers($criteria) {\n        try {\n            return $this->soapClient->getUsers(['criteria' => $criteria]);\n        } catch (SoapFault $fault) {\n            die('Error: '. $fault);\n        }\n    }\n\n    public function addUser($secureToken, $username, $password, $firstName, $lastName) {\n        try {\n            return $this->soapClient->addUser([\n                'token' => $secureToken,\n                'userDetails' => [\n                    'username' => $username,\n                    // ... other fields\n                ],\n            ]);\n        } catch (SoapFault $fault) {\n            die('Error: '. $fault);\n        }\n    }\n}