<?php\n// submit.php\n\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    // Validate and sanitize inputs\n    $userInput = filter_input(INPUT_POST, 'user', FILTER_SANITIZE_STRING);\n\n    if (!empty($userInput)) {\n        // Define the path to your executable\n        $exePath = escapeshellarg(realpath('Lib.exe'));\n\n        // Sanitize and prepare arguments\n        $argument = escapeshellarg($userInput);\n\n        // Execute the EXE with arguments\n        $output = shell_exec("$exePath $argument");\n\n        // Output or handle response as needed\n        echo "<pre>$output</pre>";\n    } else {\n        echo "Invalid input.";\n    }\n} else {\n    echo "Form not submitted properly.";\n}\n?>