<?php\n// Assuming your HTML form has checkbox names like 'checkbox1', 'checkbox2', etc.\n$checkbox1 = isset($_POST['checkbox1']) ? 1 : 0; // 1 if checked, 0 otherwise\n$checkbox2 = isset($_POST['checkbox2']) ? 1 : 0;\n\n// Similar for radio buttons\n$radio_option = isset($_POST['radio']) ? $_POST['radio'] : null;\n\n// Prepare and execute your MySQL query safely using prepared statements to prevent SQL injection\n$stmt = $pdo->prepare("INSERT INTO your_table (column_for_checkbox1, column_for_checkbox2, column_for_radio) VALUES (?, ?, ?)");\n$stmt->execute([$checkbox1, $checkbox2, $radio_option]);\n?>