$idsArray = [123, 535, 345, 567, 878]; // Your list of IDs\n\n// Create placeholders for each item in the array\n$placeholders = implode(',', array_fill(0, count($idsArray), '?'));\n\n// Prepare the SQL statement with dynamically created placeholders\n$stmt = $mysqli->prepare("SELECT foo, blar FROM table WHERE id IN ($placeholders)");\n\n// Check if prepare was successful\nif ($stmt) {\n    // Generate a string of types (e.g., 'iiiii' for 5 integers)\n    $types = str_repeat('i', count($idsArray));\n    \n    // Bind the parameters to statement variables\n    $args = array_merge([$types], $idsArray);\n    call_user_func_array(array($stmt, 'bind_param'), createRef($args));\n\n    // Execute the query\n    $stmt->execute();\n    \n    // Get result\n    $result = $stmt->get_result();\n    \n    // Process result...\n}\n\n// Function to make variables passed by reference for bind_param\nfunction createRef(&$arr) {\n    $refs = [];\n    foreach ($arr as $key => $value)\n        $refs[$key] = &$arr[$key];\n    return $refs;\n}