<?php\n\nfunction imperialToMetric($imperial) {\n    // Define conversion factors\n    $feetToMeters = 0.3048;\n    $inchToMeters = 0.0254;\n\n    // Use regular expressions to extract numbers and fractions from the string\n    preg_match_all('/(\d+)\s*\'/', $imperial, $feetMatches);\n    preg_match_all('/(\d+)\s*\"/', $imperial, $inchesMatches);\n    preg_match_all('/(\d+)\/(\d+)/', $imperial, $fractionMatches);\n\n    // Sum up feet and inches\n    $totalFeet = isset($feetMatches[1][0]) ? (int)$feetMatches[1][0] : 0;\n    $totalInches = isset($inchesMatches[1][0]) ? (int)$inchesMatches[1][0] : 0;\n\n    // Convert fractions to decimal inches\n    foreach ($fractionMatches[0] as $fraction) {\n        list($numerator, $denominator) = explode('/', $fraction);\n        $totalInches += (float)$numerator / (float)$denominator;\n    }\n\n    // Convert total feet and inches to meters\n    $totalMeters = ($totalFeet * $feetToMeters) + ($totalInches * $inchToMeters);\n\n    return $totalMeters;\n}\n\n// Example usage:\n$length1 = "3' 2 1/2\"";\necho imperialToMetric($length1); // Outputs: 0.9755 meters\n\n$length2 = "1/2\"";\necho imperialToMetric($length2); // Outputs: 0.0127 meters\n\n?>