<?php\n$servername = "your_server";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n$sql = "SELECT venues.venue_name AS Venue, COUNT(volunteers_2009.id) AS NumberOfVolunteers \n        FROM venues \n        JOIN volunteers_2009 ON venues.id = volunteers_2009.venue_id \n        GROUP BY venues.venue_name \n        ORDER BY venues.venue_name ASC";\n\n$result = $conn->query($sql);\n\nif ($result->num_rows > 0) {\n    while($row = $result->fetch_assoc()) {\n        echo '<p>' . htmlspecialchars($row['Venue']) . ': ' . htmlspecialchars($row['NumberOfVolunteers']) . '</p>';\n    }\n} else {\n    echo "No results found.";\n}\n\n$conn->close();\n?>