<?php\n    $program="/usr/bin/top -n 1";\n    echo "program:{$program}<br /> \n";\n\n    ob_start(); // Turn on Output Buffering to capture the output of system()\n    $retval = 0;\n    system($program, $retval);\n    $output = ob_get_contents(); // Collect the output\n    ob_end_clean(); \n\n    echo htmlspecialchars($output); // Safely print the command output\n\n    echo "ver1 = {$ver1}<br />\n";\n    echo "retval = {$retval}<br /> \n";\n\n    if($retval==0) {\n        echo "Returned 0<br />\n";\n    } else {\n        echo "Returned not zero <br />\n";\n    }\n\n    die;\n?>