class MemberInfo {\n    public $meta_data;\n\n    public function __get($name) {\n        if ($name === 'meta_data') {\n            // This could be encoding the array as JSON when accessed\n            return json_encode($this->meta_data);\n        }\n        throw new Exception("Property not found");\n    }\n\n    public function __set($name, $value) {\n        if ($name === 'meta_data') {\n            // Ensure the value is an array before setting it\n            if (!is_array($value)) {\n                $value = json_decode($value, true);\n            }\n            $this->meta_data = $value;\n        } else {\n            throw new Exception("Property not found");\n        }\n    }\n}