require 'vendor/autoload.php';\n\nuse PhpPurePowerpointReader\Pptx;\n\n// Load the PowerPoint file\n$ppt = new Pptx('path/to/your/presentation.pptx');\n\nif ($ppt->isReadable()) {\n    // Iterate over each slide and get the text\n    foreach ($ppt->getSlides() as $id => $slide) {\n        echo "Slide #{$id}:\n";\n        echo implode("\n", $slide['text']);\n        echo "\n\n";\n    }\n} else {\n    echo 'File is not readable.';\n}