// Setting the cookie with hashed id and salt\n$saltedHash = sha1('124' . 'mysalt');\n$_COOKIE['test'] = $saltedHash;\n\n// Preparing the SQL query to match the stored hash\n$userIdToFind = '124';\n$salt = 'mysalt';\n$saltedHashForQuery = sha1($userIdToFind . $salt);\n\n$sql = "SELECT * FROM users WHERE SHA1(CONCAT(`id`, '$salt')) = '$saltedHashForQuery'";\n\n// Alternatively, if you prefer using a parameterized query for better security\n// Assuming $pdo is an instance of PDO\n$stmt = $pdo->prepare("SELECT * FROM users WHERE SHA1(CONCAT(`id`, ?)) = ?");\n$stmt->execute([$salt, $saltedHashForQuery]);