// Inserting data safely into the database\n$sql = sprintf("INSERT INTO profiles(email, fullName) VALUES(%s,%s)",\n             $mdb2->quote($email), $mdb2->quote($fullName));\n$mdb2->query($sql);\n\n// Retrieving data from the database\n$query = "SELECT email, fullName FROM profiles WHERE email = %s";\n$safeQuery = sprintf($query, $mdb2->quote($email));\n$result = $mdb2->query($safeQuery);\n\nif (MDB2::isError($result)) {\n    die("Failed to retrieve data: " . $result->getMessage());\n}\n\nwhile ($row = $result->fetchRow(MDB2_FETCHMODE_ASSOC)) {\n    echo 'Email: ' . htmlspecialchars($row['email']) . '<br>';\n    echo 'Full Name: ' . htmlspecialchars($row['fullName']) . '<br>';\n}