// Example function to process data in chunks\nfunction processLargeDataSet($chunkSize = 100) {\n    $offset = 0;\n    do {\n        // Fetch limited rows from the database\n        $rows = fetchRowsFromDatabase($offset, $chunkSize);\n        \n        if (empty($rows)) {\n            break;\n        }\n        \n        // Process each row\n        foreach ($rows as $row) {\n            processRow($row);\n        }\n        \n        // Free memory\n        unset($rows);\n        \n        // Increase offset for the next chunk\n        $offset += $chunkSize;\n    } while (true);\n}\n\n// Example function to explicitly free memory\nfunction processRow($row) {\n    // Perform operations with row data\n    // ...\n    \n    // Unset large variables if necessary\n    // unset($largeVariable);\n}\n\n// Garbage collection can be triggered manually within loops\ngc_enable();\nprocessLargeDataSet();\ngc_collect_cycles();