// Assuming you have a working implementation of double metaphone in PHP\nfunction wordsRhymeDoubleMetaphone($word1, $word2) {\n    require_once 'DoubleMetaPhone.php'; // Your implementation or extension\n\n    $dm = new DoubleMetaPhone();\n    $key1Pair = $dm->GetCodes($word1);\n    $key2Pair = $dm->GetCodes($word2);\n\n    return ($key1Pair[0] === $key2Pair[0]) || ($key1Pair[1] === $key2Pair[1]);\n}\n\n// Example usage\n$word1 = "knight";\n$word2 = "light";\n\nif (wordsRhymeDoubleMetaphone($word1, $word2)) {\n    echo "$word1 and $word2 rhyme phonetically based on Double Metaphone.";\n} else {\n    echo "$word1 and $word2 do not rhyme phonetically based on Double Metaphone.";\n}