<?php\n\n// Set up the parameters for the method call.\n$params = array('Hello', 'World');\n\n// Create an XML-RPC request.\n$request = xmlrpc_encode_request("sample.method", $params, ['encoding' => 'utf-8']);\n\n// Send the request using HTTP GET in this example.\n$context = stream_context_create(array(\n    'http' => array(\n        'method'  => "POST",\n        'header'  => "Content-Type: text/xml",\n        'content' => $request\n    )\n));\n\n// The URL of the XML-RPC server.\n$url = "http://your.server.url/RPC2";\n\n$file = file_get_contents($url, false, $context);\n\n$response = xmlrpc_decode_response($file);\n\nif ($response === null) {\n    // Handle error.\n    error_log('An error occurred while processing your request.');\n} else {\n    echo htmlspecialchars(print_r($response, true));\n}\n\n?>