<?php\n$sql = "SELECT ARTICLE_DESC FROM Auctions WHERE ARTICLE_NO ='$pk'";\n$htmlset = mysql_query($sql) or die(mysql_error());\n$row2 = mysql_fetch_assoc($htmlset);\n\n// Escape the HTML content to be safely passed to JavaScript\n$safeHtmlContent = htmlspecialchars(json_encode($row2['ARTICLE_DESC']), ENT_QUOTES, 'UTF-8');\n\necho "<script>\ndocument.addEventListener('DOMContentLoaded', function() {\n    var popupButton = document.createElement('button');\n    popupButton.textContent = 'Open Popup';\n    popupButton.onclick = function() {\n        var child1 = window.open('', '_blank');\n        if (child1) {\n            child1.document.write(JSON.parse(" . $safeHtmlContent . "));\n            child1.document.close();\n        } else {\n            alert('Please allow pop-ups for this website.');\n        }\n    };\n    document.body.appendChild(popupButton);\n});\n</script>";\n?>