function getCategories(){\n    $prod = new C_Product();\n    $cat= $prod->getCategorieenAsArray();\n\n    $tr = "";\n    foreach ($cat as $key => $value){\n        // Assign id to the <tr>\n        $tr.="<tr id=\"row_$key\"><td>&nbsp;</td><td>$value</td><td><img class=\"deleterow\" src=\"images/delete_button.gif\"></td></tr>\n";\n    }\n    return $tr; \n}