<?php\nsession_start();\n\nif (!isset($_GET["cmd"]) || !isset($_GET["pk"])) {\n    die("You should have a 'cmd' and 'pk' parameter in your URL");\n}\n\n$cmd = $_GET["cmd"];\n$pk = $_GET["pk"];\n\n$con = mysqli_connect("localhost", "root", "geheim", "ebay");\n\nif (!$con) {\n    die('Connection failed because of: ' . mysqli_error($con));\n}\n\nif ($cmd == "GetAuctionData") {\n    $sql = "SELECT * FROM Auctions WHERE ARTICLE_NO = ?";\n    \n    if ($stmt = mysqli_prepare($con, $sql)) {\n        mysqli_stmt_bind_param($stmt, "i", $pk);\n        mysqli_stmt_execute($stmt);\n        $result = mysqli_stmt_get_result($stmt);\n\n        echo "<table border='1' width='100%'>";\n        echo "<tr>";\n        echo "<th>Username</th>";\n        echo "<th>Start Date</th>";\n        echo "<th>Description</th>";\n        echo "</tr>";\n\n        while ($row = mysqli_fetch_array($result)) {\n            echo "<tr>";\n            echo "<td>" . htmlspecialchars($row['USERNAME']) . "</td>";\n            echo "<td>" . htmlspecialchars($row['ACCESSSTARTS']) . "</td>";\n            echo "<td>" . htmlspecialchars($row['ARTICLE_DESC']) . "</td>";\n            echo "</tr>";\n\n            // Save the picture URL for display after the table\n            $pic_url = htmlspecialchars($row['PIC_URL']);\n        }\n        echo "</table>";\n        \n        if (!empty($pic_url)) {\n            echo "<img src='$pic_url' alt='Auction Image'>";\n        } else {\n            echo "<p>No image available</p>";\n        }\n\n        mysqli_stmt_close($stmt);\n    } else {\n        die("Error preparing statement.");\n    }\n}\n\nmysqli_close($con);\n?>