// Assuming $stmt is your prepared statement and it has been executed\n$result = db2_exec($conn, "SELECT SALES, ITEM FROM your_table");\n\n$data = array();\nwhile ($row = db2_fetch_assoc($result)) {\n    // Convert each row to the format required by FusionCharts\n    $data[] = array('label' => $row['ITEM'], 'value' => $row['SALES']);\n}\n\n// Create XML data for FusionCharts\n$xmlData = "<chart>";\nforeach ($data as $item) {\n    $xmlData .= "<set label='" . htmlspecialchars($item['label']) . "' value='" . htmlspecialchars($item['value']) . "' />";\n}\n$xmlData .= "</chart>";\n\n$FC->setXMLData($xmlData);