<?php\n$packageName = 'Validate';\n$url = "https://pear.php.net/package/$packageName/atom";\n\n$xml = simplexml_load_file($url);\nif ($xml === false) {\n    die('Error loading feed');\n}\n\n// Get latest version\n$latestVersion = (string)$xml->entry[0]['version'];\n\necho "Latest version of the $packageName package is: $latestVersion\n";\necho "Release Date: ", substr((string)$xml->entry[0]->updated, 0, 10), "\n";\n?>