<?php\nsession_start();\n$inactive = 180; // time in seconds (3 minutes)\nif (isset($_SESSION['timeout'])) {\n    $elapsed_time = time() - $_SESSION['timeout'];\n    if ($elapsed_time >= $inactive) {\n        session_unset();\n        session_destroy();\n        echo json_encode(array('sessionExpired' => true));\n        exit;\n    }\n}\n$_SESSION['timeout'] = time(); // Reset timeout\necho json_encode(array('sessionExpired' => false));\n?>