<?php\n// Example array simulating data from the database\n$projects = [\n    'Project 1',\n    'Project 2',\n    'Project 3',\n    'Project 4',\n];\n\n// Array to hold IDs corresponding to each list item\n$projectIds = ['one', 'two', 'three', 'four', 'five', 'six', 'seven', 'eight', 'nine', 'ten', 'eleven', 'twelve'];\n\necho '<ul id="work_div">';\nforeach ($projects as $index => $project) {\n    // Ensure we don't exceed the number of available IDs\n    if (isset($projectIds[$index])) {\n        echo '<li id="' . htmlspecialchars($projectIds[$index]) . '"><a href="detail/' . htmlspecialchars(urlencode(strtolower(str_replace(' ', '-', $project)))) . '">' . htmlspecialchars($project) . '</a></li>';\n    } else {\n        // Handle the case where there are more projects than predefined IDs\n        echo '<li><a href="detail/' . htmlspecialchars(urlencode(strtolower(str_replace(' ', '-', $project)))) . '">' . htmlspecialchars($project) . '</a></li>';\n    }\n}\necho '</ul>';\n?>