$dbAdapter = Zend_Db_Table::getDefaultAdapter();\n\ntry {\n    // Perform the insert operation\n    $insertResult = $dbAdapter->insert('your_table_name', [\n        'column1' => 'value1',\n        'column2' => 'value2'\n    ]);\n\n    // Get the last inserted ID immediately after the insert\n    $lastInsertId = $dbAdapter->lastInsertId();\n\n    echo "Last Inserted ID: " . $lastInsertId;\n\n} catch (Exception $e) {\n    // Handle exceptions if any\n    echo "Error: " . $e->getMessage();\n}